mapper.insert:
INSERT INTO users (
    email, password, real_name, nick_name,
    birth_date, rating, create_time
) VALUES (
    #{email}, #{password}, #{real_name}, #{nick_name},
    #{birth_date}, #{rating}, #{create_time}
)

mapper.update:
UPDATE users
SET password = #{password, jdbcType=VARCHAR}
WHERE email = #{email}
